mapper.insert:
INSERT INTO students (
id, student_number, name, class_name, email, phone, major, grade, enrollment_year,
) VALUES (
#{id}, #{studentNumber}, #{name}, #{className}, #{email}, #{phone}, #{major}, #{grade}, #{enrollmentYear},
)

mapper.update:
UPDATE students
student_number = #{studentNumber}, name = #{name}, email = #{email}, phone = #{phone}, class_name = #{className}, major = #{major}, grade = #{grade}, enrollment_year = #{enrollmentYear},
WHERE id = #{id}

mapper.select:
SELECT id, student_number, name, email, phone, class_name, major, grade, enrollment_year
FROM students
WHERE student_number = #{studentNumber}

SELECT id, student_number, name, email, phone, class_name, major, grade, enrollment_year
FROM students
WHERE id = #{id}

SELECT id, student_number, name, email, phone, class_name, major, grade, enrollment_year
FROM students
WHERE class_name = #{className}
